function fav_control(id) {
    var n = $("a[data-id=" + id + "]");
    if (n.hasClass("unfollowed")) {
        $.ajax({
            url: "ajax/fav.aspx?t=1&id=" + id,
            success: function (e) {
                if (e == "yes") {
                    n.removeClass("unfollowed");
                    n.addClass("followed");
                } else {
                    alert('收藏失败。');
                }
            }
        })
    }else if (n.hasClass("followed")) {
        $.ajax({
            url: "ajax/fav.aspx?t=2&id=" + id,
            success: function (e) {
                if (e == "yes") {
                    n.removeClass("followed");
                    n.addClass("unfollowed");
                } else {
                    alert('取消收藏失败。');
                }
            }
        })
    }
}

function config_control(id) {
    $.ajax({
        url: "ajax/site.aspx?id=" + id,
        success: function (e) {
            if (e == null || e=="") {
            $("#ConfigErrorModal").modal("show");
            } else {
            var dataObj = eval("(" + e + ")");
            $("#siteid").val(id);
            $("#sitename").val(dataObj.sitename);
            $("#siteurl").val(dataObj.siteurl);
            $.each(dataObj.sitetag, function (idx, item) {
                $("#tag" + item.tagid).attr("checked", "checked");
            })
            $("#ConfigModal").modal("show");
        }}
    })
}

function config_submit() {
    var id = $("#siteid").val();
    var a = $("#sitename").val();
    var b = $("#siteurl").val();
    var c = "";
    $('input[name="tagcheck"]:checked').each(function () {
        c += $(this).val() + ',';
    });
    var d = $("#siteimgurl").val();
    $.ajax({
        url: "ajax/fav.aspx?t=3&id=" + id + "&url=" + b + "&imgurl=" + d + "&name=" + a + "&tags=" + c,
        success: function (e) {
            if (e == "yes") {
                $("#ConfigModal").modal("hide");
            } else {
                alert('修改失败。');
            }
        }
    })
}

function manage_control(id) {
    $.ajax({
        url: "ajax/site.aspx?t=manage&id=" + id,
        success: function (e) {
                var dataObj = eval("(" + e + ")");
                $("#siteid").val(id);
                $("#sitename").val(dataObj.sitename);
                $("#siteimg").attr("src", dataObj.siteimg);
                $("#siteimgurl").val(dataObj.siteimg);
                $("#siteurl").val(dataObj.siteurl);
                $.each(dataObj.sitetag, function (idx, item) {
                    $("#tag" + item.tagid).attr("checked", "checked");
                })
                $("#ConfigModal").modal("show");
        }
    })
} 

function manage_upload() {
    $.ajaxFileUpload
    (
        {
            url: 'ajax/file.aspx', //用于文件上传的服务器端请求地址
            secureuri: false, //是否需要安全协议，一般设置为false
            fileElementId: 'imgfilepath', //文件上传域的ID
            dataType: 'json', //返回值类型 一般设置为json
            success: function (data, status)  //服务器成功响应处理函数
            {
                $("#siteimg").attr("src", data.imgurl);
                $("#siteimgurl").val(data.imgurl);
                if (typeof (data.error) != 'undefined') {
                    if (data.error != '') {
                        alert(data.error);
                    }
                }
            },
            error: function (data, status, e)//服务器响应失败处理函数
            {
                alert(e);
            }
        }
    )
    return false;
}

